import { useEffect, useRef } from 'react'

export default function GetBellied() {

    const canvasRef = useRef(null)

    useEffect(() => {

        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
        let particlesArray = [];

        let settings = {
            radius: 100,
            imageSize: 5,
            pixelSize: 2,
            speed: 20,
            randomSpeed: true,
            returnSpeed: 0.2
        }

        // mouse
        let mouse = {
            x: null,
            y: null,
            radius: settings.radius,
        }

        const mouseMoveEventListener = event => {
            mouse.x = event.x + canvas.clientLeft / 2;
            mouse.y = event.y + canvas.clientTop / 2;
        }

        const resizeEventListener = event => {
            canvas.width = innerWidth;
            canvas.height = innerHeight;
            init();
        }

        window.addEventListener('mousemove', mouseMoveEventListener);

        function drawImage(img) {
            class Particle {
                constructor(x, y, color, maxDistance, transform, size, speed, randomSpeed, returnSpeed) {
                    this.x = x * transform + canvas.width / 2 - img.width * transform / 2;
                    this.y = y * transform + canvas.height / 2 - img.height * transform / 2;
                    this.baseX = this.x;
                    this.baseY = this.y;
                    this.color = color;
                    this.size = size || 2;
                    this.speed = speed ? randomSpeed ? (Math.random() * speed) : speed : (Math.random() * 5) + 2;
                    this.maxDistance = maxDistance;
                    this.returnSpeed = returnSpeed || 0.1;
                }
                draw() {
                    const halfSize = this.size / 2;
                    ctx.fillStyle = this.color;
                    ctx.beginPath();
                    ctx.rect(this.x, this.y, this.size, this.size);
                    ctx.fill();
                }
                update() {
                    let dx = mouse.x - this.x;
                    let dy = mouse.y - this.y;
                    let distance = Math.sqrt(dx * dx + dy * dy);
                    let forceDirectionX = dx / distance;
                    let forceDirectionY = dy / distance;
                    let force = (this.maxDistance - distance) / this.maxDistance;
                    if (force < 0) force = 0;
                    let directionX = (forceDirectionX * force * this.speed);
                    let directionY = (forceDirectionY * force * this.speed);
                    if (distance < mouse.radius + this.size) {
                        this.x -= directionX;
                        this.y -= directionY;
                    } else {
                        if (this.x !== this.baseX) {
                            let dx = this.x - this.baseX;
                            this.x -= dx * this.returnSpeed;
                        }
                        if (this.y !== this.baseY) {
                            let dy = this.y - this.baseY;
                            this.y -= dy * this.returnSpeed;
                        }
                    }
                    this.draw();
                }
            }

            function init() {
                particlesArray = [];
                mouse.radius = settings.radius;
                for (let y = 0, y2 = data.height; y < y2; y++) {
                    for (let x = 0, x2 = data.width; x < x2; x++) {
                        if (data.data[(y * 4 * data.width) + (x * 4) + 3] > 128) {
                            let positionX = x;
                            let positionY = y;
                            let color = `rgb(${data.data[(y * 4 * data.width) + (x * 4)]},${data.data[(y * 4 * data.width) + (x * 4) + 1]},${data.data[(y * 4 * data.width) + (x * 4) + 2]})`;
                            particlesArray.push(
                                new Particle(
                                    positionX,
                                    positionY,
                                    color,
                                    settings.radius,
                                    settings.imageSize,
                                    settings.pixelSize,
                                    settings.speed,
                                    settings.randomSpeed,
                                    settings.returnSpeed
                                )
                            );
                        }
                    }
                }
            }

            function animate() {
                requestAnimationFrame(animate);
                ctx.fillStyle = `rgba(0, 0, 0, 0.5)`;
                ctx.fillRect(0, 0, innerWidth, innerHeight);

                for (let i = 0; i < particlesArray.length; i++) {
                    particlesArray[i].update();
                }
            }

            let imageWidth = img.width;
            let imageHeight = img.height;
            ctx.drawImage(img, 0, 0)
            const data = ctx.getImageData(0, 0, imageWidth, imageHeight);
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            init();
            animate();

            window.addEventListener('resize', resizeEventListener);
        }

        const img = new window.Image();
        img.src = 'data:image/png;base64,';
        img.onload = () => drawImage(img);

        return () => {
            window.removeEventListener("resize", resizeEventListener) // this event listener is removed after the new route loads
            window.removeEventListener("mousemove", mouseMoveEventListener) // this event listener is removed after the new route loads
        }
    })

    return (
        <canvas ref={canvasRef} styles="position: absolute; top: 0; left: 0; width: 100%; height: 100%;"></canvas>
    )
}